DELIMITER / /

CREATE PROCEDURE `DefinirConexaoPadrao`(IN conexaoId INT, IN ambienteId INT)
BEGIN
    DECLARE conexaoExiste INT DEFAULT 0;
    
    -- Verificar se a conexÃ£o existe
    SELECT COUNT(*) INTO conexaoExiste 
    FROM config_db_connections 
    WHERE id = conexaoId AND ambiente_id = ambienteId;
    
    IF conexaoExiste > 0 THEN
        -- Remover o status de padrÃ£o de todas as conexÃµes do mesmo ambiente
        UPDATE config_db_connections 
        SET padrao = 0 
        WHERE ambiente_id = ambienteId;
        
        -- Definir a conexÃ£o especÃ­fica como padrÃ£o
        UPDATE config_db_connections 
        SET padrao = 1 
        WHERE id = conexaoId AND ambiente_id = ambienteId;
        
        SELECT CONCAT('ConexÃ£o ID ', conexaoId, ' definida como padrÃ£o para o ambiente ID ', ambienteId) AS resultado;
    ELSE
        SELECT 'Erro: ConexÃ£o nÃ£o encontrada' AS resultado;
    END IF;
END//

