DROP TABLE IF EXISTS `meufinanceiro`.`funforcli_contas_bancarias`;
CREATE TABLE `meufinanceiro`.`funforcli_contas_bancarias` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cliente_id` int(11) NOT NULL COMMENT 'Referência ao cliente na tabela funforcli',
  `banco` varchar(100) NOT NULL,
  `codigo_banco` varchar(10) DEFAULT NULL,
  `agencia` varchar(20) NOT NULL,
  `conta` varchar(30) NOT NULL,
  `tipo_conta` enum('corrente','poupanca','salario','investimento') NOT NULL DEFAULT 'corrente',
  `operacao` varchar(10) DEFAULT NULL COMMENT 'Para contas que exigem campo de operação',
  `titular` varchar(255) NOT NULL,
  `cpf_cnpj_titular` varchar(20) NOT NULL,
  `principal` tinyint(1) DEFAULT 0 COMMENT 'Indica se é a conta principal',
  `observacoes` text DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `empresa_id` int(11) NOT NULL COMMENT 'Empresa a que pertence este registro',
  `sync_data` timestamp NOT NULL DEFAULT current_timestamp(),
  `sync_hash` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `sync_status` enum('pendente','sincronizado','erro') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'pendente',
  PRIMARY KEY (`id`),
  KEY `cliente_id` (`cliente_id`),
  CONSTRAINT `fk_contas_bancarias_cliente` FOREIGN KEY (`cliente_id`) REFERENCES `meufinanceiro`.`funforcli` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;

