CREATE TABLE `meufinanceiro`.`lancamento_status` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `empresa_id` int(11) NOT NULL,
  `origem` enum('pdv','lancamentos','delivery','site','app_garcom','geral') NOT NULL DEFAULT 'geral',
  `codigo` varchar(50) NOT NULL,
  `nome` varchar(100) NOT NULL,
  `descricao` text DEFAULT NULL,
  `cor` varchar(7) NOT NULL DEFAULT '#007bff',
  `icone` varchar(50) DEFAULT NULL,
  `ordem` int(11) NOT NULL DEFAULT 0,
  `ativo` tinyint(1) NOT NULL DEFAULT 1,
  `padrao` tinyint(1) NOT NULL DEFAULT 0,
  `permite_edicao` tinyint(1) NOT NULL DEFAULT 1,
  `permite_exclusao` tinyint(1) NOT NULL DEFAULT 1,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `sync_data` timestamp NOT NULL DEFAULT current_timestamp(),
  `sync_hash` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `sync_status` enum('pendente','sincronizado','erro') CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT 'pendente',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_empresa_origem_codigo` (`empresa_id`,`origem`,`codigo`),
  KEY `idx_ativo` (`ativo`),
  KEY `idx_empresa_origem` (`empresa_id`,`origem`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;

