DROP TABLE IF EXISTS `meufinanceiro`.`sync_logs`;
CREATE TABLE `meufinanceiro`.`sync_logs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `empresa_id` int(11) NOT NULL,
  `tabela` varchar(50) NOT NULL,
  `tipo_sync` enum('completa','incremental','manual') NOT NULL,
  `direcao` enum('import','export','bidirectional') NOT NULL,
  `inicio` timestamp NOT NULL DEFAULT current_timestamp(),
  `fim` timestamp NULL DEFAULT NULL,
  `registros_processados` int(11) DEFAULT 0,
  `registros_inseridos` int(11) DEFAULT 0,
  `registros_atualizados` int(11) DEFAULT 0,
  `registros_erro` int(11) DEFAULT 0,
  `status` enum('executando','sucesso','erro','cancelado') DEFAULT 'executando',
  `detalhes` text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL,
  `erro_mensagem` text DEFAULT NULL,
  `sync_status` varchar(20) DEFAULT 'pendente',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `sync_data` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `idx_sync` (`sync_status`,`sync_data`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci ROW_FORMAT=DYNAMIC;

