#
# Procedure "ConfigurarSincronizacao"
#

DROP PROCEDURE IF EXISTS `meufinanceiro`.`ConfigurarSincronizacao`;
CREATE PROCEDURE `meufinanceiro`.`ConfigurarSincronizacao`()
BEGIN
    DECLARE done INT DEFAULT FALSE;
    DECLARE tabela_nome VARCHAR(255);
    DECLARE error_occurred INT DEFAULT FALSE;
    
    -- Cursor para pegar todas as tabelas do banco
    DECLARE cursor_tabelas CURSOR FOR 
        SELECT TABLE_NAME 
        FROM information_schema.TABLES 
        WHERE TABLE_SCHEMA = DATABASE()
        AND TABLE_TYPE = 'BASE TABLE';
    
    -- Handler para erros SQL
    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION
    BEGIN
        SET error_occurred = TRUE;
        GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
        SET @full_error = CONCAT('ERROR ', @errno, ' (', @sqlstate, '): ', @text);
        SELECT @full_error; -- Para debug
    END;
        
    -- Handler para quando o cursor terminar
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
    
    -- Abre o cursor
    OPEN cursor_tabelas;
    
    -- Loop através das tabelas
    read_loop: LOOP
        -- Pega próxima tabela
        FETCH cursor_tabelas INTO tabela_nome;
        
        -- Sai do loop se terminou
        IF done THEN
            LEAVE read_loop;
        END IF;
        
        -- Verifica se a tabela já tem as colunas de sincronização
        IF NOT EXISTS (
            SELECT 1 
            FROM information_schema.COLUMNS 
            WHERE TABLE_SCHEMA = DATABASE() 
            AND TABLE_NAME = tabela_nome 
            AND COLUMN_NAME = 'sync_status'
        ) THEN
            BEGIN
                DECLARE CONTINUE HANDLER FOR SQLEXCEPTION 
                BEGIN
                    SET error_occurred = TRUE;
                    GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
                    SET @full_error = CONCAT('Erro ao adicionar colunas em ', tabela_nome, ': ', @text);
                    SELECT @full_error;
                END;

                SET @sql = CONCAT('ALTER TABLE `', tabela_nome, '` 
                    ADD COLUMN IF NOT EXISTS `sync_status` VARCHAR(20) DEFAULT "pendente",
                    ADD COLUMN IF NOT EXISTS `created_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
                    ADD COLUMN IF NOT EXISTS `updated_at` DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
                    ADD COLUMN IF NOT EXISTS `sync_data` DATETIME NULL');
                    
                PREPARE stmt FROM @sql;
                EXECUTE stmt;
                DEALLOCATE PREPARE stmt;
            END;
        END IF;
        
        -- Remove triggers existentes se houver
        SET @drop_trigger1 = CONCAT('DROP TRIGGER IF EXISTS before_insert_', tabela_nome);
        SET @drop_trigger2 = CONCAT('DROP TRIGGER IF EXISTS before_update_', tabela_nome);
        
        PREPARE stmt FROM @drop_trigger1;
        EXECUTE stmt;
        PREPARE stmt FROM @drop_trigger2;
        EXECUTE stmt;
        
        -- Cria trigger para INSERT
        BEGIN
            DECLARE CONTINUE HANDLER FOR SQLEXCEPTION 
            BEGIN
                SET error_occurred = TRUE;
                GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
                SET @full_error = CONCAT('Erro ao criar trigger INSERT para ', tabela_nome, ': ', @text);
                SELECT @full_error;
            END;

            SET @create_trigger = CONCAT('
                CREATE TRIGGER `before_insert_', tabela_nome, '`
                BEFORE INSERT ON `', tabela_nome, '`
                FOR EACH ROW 
                BEGIN
                    IF NEW.sync_status IS NULL THEN
                        SET NEW.sync_status = "pendente";
                    END IF;
                    IF NEW.created_at IS NULL THEN
                        SET NEW.created_at = NOW();
                    END IF;
                    IF NEW.updated_at IS NULL THEN
                        SET NEW.updated_at = NOW();
                    END IF;
                END');
                
            PREPARE stmt FROM @create_trigger;
            EXECUTE stmt;
            DEALLOCATE PREPARE stmt;
        END;
        
        -- Cria trigger para UPDATE
        BEGIN
            DECLARE CONTINUE HANDLER FOR SQLEXCEPTION 
            BEGIN
                SET error_occurred = TRUE;
                GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
                SET @full_error = CONCAT('Erro ao criar trigger UPDATE para ', tabela_nome, ': ', @text);
                SELECT @full_error;
            END;

            SET @create_trigger = CONCAT('
                CREATE TRIGGER `before_update_', tabela_nome, '`
                BEFORE UPDATE ON `', tabela_nome, '`
                FOR EACH ROW 
                BEGIN
                    IF NEW.sync_data IS NULL OR OLD.updated_at <> NEW.updated_at THEN
                        SET NEW.sync_status = "pendente";
                    END IF;
                    SET NEW.updated_at = NOW();
                END');
                
            PREPARE stmt FROM @create_trigger;
            EXECUTE stmt;
            DEALLOCATE PREPARE stmt;
        END;
        
    END LOOP;
    
    -- Fecha o cursor
    CLOSE cursor_tabelas;
    
END;
